import React from 'react';
import { createStore, createEvent } from 'effector';
import { createComponent } from 'effector-react';
import ProductList from '../product-list';
import './cell-list.css';

// const fakeData = [
//   { barcode: 123, products: [
//     { product_barcode: 'aaasdsd', amount: 400 },
//     { product_barcode: 'asdfdsf', amount: 200 }
//   ]},
//   { barcode: 341, products: [
//     { product_barcode: 'aaasdsd', amount: 400 },
//     { product_barcode: 'asdfdsf', amount: 200 }
//   ]},
//   { barcode: 234, products: [
//     { product_barcode: 'asdf', amount: 300 },
//     { product_barcode: 'adsf', amount: 100 }
//   ]}
// ];

// ниже определить функции работы со стором

const cellListStore = createStore([]);
const addCellToList = createEvent('add cell to list');
const addProductToList = createEvent('add product to list');
const takeProductFromList = createEvent('take product from list');

// тут надо доработать логику, чтобы не передавать state целиком
cellListStore
  .on(addCellToList, (state, payload) => [...state, payload])
  .on(addProductToList, (state, payload) => [...state, payload])
  .on(takeProductFromList, (state, payload) => [...state, payload]);

const CellList = createComponent(
  cellListStore, (props, cellList) => {

    return (
      <div className="cell-list">
        {
          cellList.map(
            (({ barcode, products }) => {
              return (
                <div key={barcode} className="cell-item">
                  <h4>Cell: {barcode}</h4>
                  <ProductList products={products} />
                </div>
              )
            })
          )
        }
      </div>
    )
  }
);

export default CellList;
