import React from 'react';
import { createStore, createEvent } from 'effector';
import { createComponent } from 'effector-react';
import { addNewCellAction } from '../cell-list';
import styled from 'styled-components';

const addCellInput = createStore('');
const changeAddCellInput = createEvent('change add cell input');
const resetField = createEvent('reset field');

addCellInput
  .on(changeAddCellInput, (state, payload) => payload)
  .reset(resetField);

const AddCellForm = createComponent(
  addCellInput, (props, text) => {
    const addNewCell = (event) => {
      event.preventDefault();
      const barcode = addCellInput.getState().trim();
      addNewCellAction(barcode);
      resetField();
    };

    return (
      <form className="add-cell-form">
        <Label>
          <span>
            Cell Barcode
          </span>
          <input 
            type="text"
            value={text}
            placeholder="12345"
            onChange={event => changeAddCellInput(event.target.value)} />
        </Label>

        <button 
          className="btn btn-outline-secondary"
          onClick={addNewCell}>
          Add Cell
        </button>
    </form>
    )
  }
);

const Label = styled.label`
  > span {
    margin-left: 10px;
    font-size: 20px;
    font-weight: 600;
    color: #E4F1FE;
    vertical-align: bottom;
    text-transform: uppercase;
  }

  > input {
    display: inline-block;
    width: 200px;
    height: 40px;

    border: none;
    outline: none;
    border-bottom: 5px solid #BDC3C7;
    background-color: #34495E;

    font-size: 20px;
    font-weight: 600;
    color: #E4F1FE;
  }

  margin-bottom: 10px;
`;

const ButtonWrapper = styled.div`
  display: flex;
  margin-top: 30px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  width: 600px;
  padding: 5px;
  padding-bottom: 0;
  margin-right: 20px;
  border-left: 10px solid #FDE3A7;
`;

export default AddCellForm;
