import React from 'react';
import { createStore, createEvent } from 'effector';
import { createComponent } from 'effector-react';
import styled from 'styled-components';

const productBarcodeInput = createStore('');
const changeProductBarcodeInput = createEvent('product barcode product form');
const resetProductBarcode = createEvent('reset field product form');

productBarcodeInput
  .on(changeProductBarcodeInput, (state, payload) => payload)
  .reset(resetProductBarcode);

const ProductBarcodeInput = createComponent(
  productBarcodeInput, (props, text) => {
    return (
      <Label>
        <input 
          type="text"
          value={text}
          placeholder="12345"
          onChange={event => changeProductBarcodeInput(event.target.value)}
        />
        <span>
          Product Barcode
        </span>
      </Label>
    )
  }
);

const Label = styled.label`
  > span {
    margin-left: 10px;
    font-size: 20px;
    font-weight: 600;
    color: #E4F1FE;
    vertical-align: bottom;
    text-transform: uppercase;
  }

  > input {
    display: inline-block;
    width: 200px;
    height: 40px;

    border: none;
    outline: none;
    border-bottom: 5px solid #BDC3C7;
    background-color: #34495E;

    font-size: 20px;
    font-weight: 600;
    color: #E4F1FE;
  }

  margin-bottom: 10px;
`;

export {
  ProductBarcodeInput,
  productBarcodeInput,
  resetProductBarcode
};
