import React from 'react';
import { createStore, createEvent } from 'effector';
import { createComponent } from 'effector-react';
import styled from 'styled-components';

const amountInput = createStore('');
const changeAmountInput = createEvent('amount input product form');
const resetAmount = createEvent('reset amount field product form');

amountInput
  .on(changeAmountInput, (state, payload) => payload)
  .reset(resetAmount);

  const AmountInput = createComponent(
    amountInput, (props, text) => {
      return (
        <Label>
          <input 
            type="text"
            value={text}
            placeholder="20"
            onChange={event => changeAmountInput(event.target.value)}
          />
          <span>
            Amount
          </span>
        </Label>
      )
    }
  );

  const Label = styled.label`
  > span {
    margin-left: 10px;
    font-size: 20px;
    font-weight: 600;
    color: #E4F1FE;
    vertical-align: bottom;
    text-transform: uppercase;
  }

  > input {
    display: inline-block;
    width: 200px;
    height: 40px;

    border: none;
    outline: none;
    border-bottom: 5px solid #BDC3C7;
    background-color: #34495E;

    font-size: 20px;
    font-weight: 600;
    color: #E4F1FE;
  }

  margin-bottom: 10px;
`;

export {
  AmountInput,
  amountInput,
  resetAmount
};
