import React from 'react';
import { GeneralForm } from '../general-styled-components';
import { addNewProductAction } from '../cell-list';
import styled from 'styled-components';

import {
  CellBarcodeInput,
  cellBarcodeInput as cellBarcodeStore,
  resetCell
} from './cell-barcode-input';

import {
  ProductBarcodeInput,
  productBarcodeInput as productBarcodeStore,
  resetProduct
} from './product-barcode-input';

import {
  AmountInput,
  amountInput as amountStore,
  resetAmount
} from './amount-input';


const isNumeric = (n) => {
  return !isNaN(parseFloat(n)) && isFinite(n);
};

const vaildateFormInputs = (cellBarcode, productBarcode, amount) => {
  let message = '';

  if (cellBarcode.trim() === '' 
      || productBarcode.trim() === '' 
      || amount.trim() === '') {
    message += `Пожалуйста, заполните все поля. `;
    return message;
  };

  if (!isNumeric(amount) || amount === '0') {
    message += `Поле "Amount" должно быть числом и не может принимать значение 0`;
    resetAmount();
  };

  return message;
}

const AddProductForm = () => {
  const addProduct = (event) => {
    event.preventDefault();
    const cellBarcode = cellBarcodeStore.getState();
    const productBarcode = productBarcodeStore.getState();
    const amount = amountStore.getState();
    const message = vaildateFormInputs(cellBarcode, productBarcode, amount);
    if (message) {
      alert(message);
      return;
    } else {
      addNewProductAction(cellBarcode, productBarcode, amount);
      resetProduct();
      resetAmount();

      resetCell();
    }
  };

  const takeProduct = (event) => {
    event.preventDefault();
    const cellBarcode = cellBarcodeStore.getState();
    const productBarcode = productBarcodeStore.getState();
    const amount = amountStore.getState();
    const message = vaildateFormInputs(cellBarcode, productBarcode, amount);
    if (message) {
      alert(message);
      return;
    } else {
      addNewProductAction(cellBarcode, productBarcode, -amount);
      resetProduct();
      resetAmount();
      resetCell();
    }
  };


  return (
    <GeneralForm>
      <CellBarcodeInput />
      <ProductBarcodeInput />
      <AmountInput />

      <ButtonWrapper>
        <button
          className="btn btn-outline-secondary"
          onClick={addProduct}>
          Add Product
        </button>
        <button
          className="btn btn-outline-secondary"
          onClick={takeProduct}>
          Take Product
        </button>
      </ButtonWrapper>
    </GeneralForm>
  )
};

const ButtonWrapper = styled.div`
  display: flex;
  margin-top: 30px;

  > button {
    display: inline-block;

    width: 200px;
    padding: 10px;
    margin-right: 10px;

    font-size: 20px;
    font-weight: 600;
    color: #34495E;

    &:hover {
      cursor: pointer;
    }
  }
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  width: 450px;
  padding: 5px;
  padding-bottom: 0;
  margin-right: 20px;
  margin-bottom: 40px;
`;

export default AddProductForm;
