import React from 'react';
import { createStore, createEvent } from 'effector';
import { createComponent } from 'effector-react';
import { addNewProductAction } from '../cell-list';
import './add-product-form.css';

const cellBarcodeInput = createStore('');
const productBarcodeInput = createStore('');
const amountInput = createStore('');

const changeCellBarcodeInput = createEvent('cell barcode product form');
const changeProductBarcodeInput = createEvent('product barcode product form');
const changeAmountInput = createEvent('amount input product form');
const resetField = createEvent('reset field product form');
const resetAmountField = createEvent('reset amount field product form');

cellBarcodeInput
  .on(changeCellBarcodeInput, (state, payload) => payload)
  .reset(resetField);

productBarcodeInput
  .on(changeProductBarcodeInput, (state, payload) => payload)
  .reset(resetField);

amountInput
  .on(changeAmountInput, (state, payload) => payload)
  .reset(resetAmountField);


const CellBarcodeInput = createComponent(
  cellBarcodeInput, (props, text) => {
    return (
      <label>
        Cell Barcode
        <input 
          type="text"
          value={text}
          onChange={event => changeCellBarcodeInput(event.target.value)} 
        />
      </label>
    )
  }
);

const ProductBarcodeInput = createComponent(
  productBarcodeInput, (props, text) => {
    return (
      <label>
        Product Barcode
        <input 
          type="text"
          value={text}
          onChange={event => changeProductBarcodeInput(event.target.value)}
        />
      </label>
    )
  }
);

const AmountInput = createComponent(
  amountInput, (props, text) => {
    return (
      <label>
        Amount
        <input 
          type="text"
          value={text}
          onChange={event => changeAmountInput(event.target.value)}
        />
      </label>
    )
  }
);

const isNumeric = (n) => {
  return !isNaN(parseFloat(n)) && isFinite(n);
};

const vaildateFormInputs = (cellBarcode, productBarcode, amount) => {
  let message = '';

  if (cellBarcode.trim() === '' || productBarcode.trim() === '' || amount.trim() === '') {
    message += `Пожалуйста, заполните все поля. `;
    return message;
  };

  if (!isNumeric(amount) || amount === '0') {
    message += `Поле "Amount" должно быть числом и не может принимать значение 0`;
    resetAmountField();
  };

  return message;
}

const AddProductForm = () => {
  const addProduct = (event) => {
    event.preventDefault();
    const cellBarcode = cellBarcodeInput.getState();
    const productBarcode = productBarcodeInput.getState();
    const amount = amountInput.getState();
    const message = vaildateFormInputs(cellBarcode, productBarcode, amount);
    if (message) {
      alert(message);
      return;
    } else {
      addNewProductAction(cellBarcode, productBarcode, amount);
      resetField();
    }
  };

  const takeProduct = (event) => {
    event.preventDefault();
    const cellBarcode = cellBarcodeInput.getState();
    const productBarcode = productBarcodeInput.getState();
    const amount = amountInput.getState();
    const message = vaildateFormInputs(cellBarcode, productBarcode, amount);
    if (message) {
      alert(message);
      return;
    } else {
      addNewProductAction(cellBarcode, productBarcode, -amount);
      resetField();
    }
  };


  return (
    <form className="add-product-form">
      <CellBarcodeInput />
      <ProductBarcodeInput />
      <AmountInput />

      <button 
        className="btn btn-outline-secondary"
        onClick={addProduct}>
        Add Product
      </button>
      <button 
        className="btn btn-outline-secondary"
        onClick={takeProduct}>
        Take Product
      </button>
    </form>
  )
};

export default AddProductForm;
