import React from 'react';
import { createStore, createEvent } from 'effector';
import { createComponent } from 'effector-react';
import { addNewProductAction } from '../cell-list';
import styled from 'styled-components';

import {
  CellBarcodeInput,
  cellBarcodeInput } from './cell-barcode-input';


const amountInput = createStore('');


const changeAmountInput = createEvent('amount input product form');
const resetField = createEvent('reset field product form');
const resetAmountField = createEvent('reset amount field product form');



amountInput
  .on(changeAmountInput, (state, payload) => payload)
  .reset(resetAmountField);



const AmountInput = createComponent(
  amountInput, (props, text) => {
    return (
      <Label>
        <input 
          type="text"
          value={text}
          placeholder="20"
          onChange={event => changeAmountInput(event.target.value)}
        />
        <span>
          Amount
        </span>
      </Label>
    )
  }
);

const isNumeric = (n) => {
  return !isNaN(parseFloat(n)) && isFinite(n);
};

const vaildateFormInputs = (cellBarcode, productBarcode, amount) => {
  let message = '';

  if (cellBarcode.trim() === '' 
      || productBarcode.trim() === '' 
      || amount.trim() === '') {
    message += `Пожалуйста, заполните все поля. `;
    return message;
  };

  if (!isNumeric(amount) || amount === '0') {
    message += `Поле "Amount" должно быть числом и не может принимать значение 0`;
    resetAmountField();
  };

  return message;
}

const AddProductForm = () => {
  const addProduct = (event) => {
    event.preventDefault();
    const cellBarcode = cellBarcodeInput.getState();
    const productBarcode = productBarcodeInput.getState();
    const amount = amountInput.getState();
    const message = vaildateFormInputs(cellBarcode, productBarcode, amount);
    if (message) {
      alert(message);
      return;
    } else {
      addNewProductAction(cellBarcode, productBarcode, amount);
      resetField();
      resetAmountField();
    }
  };

  const takeProduct = (event) => {
    event.preventDefault();
    const cellBarcode = cellBarcodeInput.getState();
    const productBarcode = productBarcodeInput.getState();
    const amount = amountInput.getState();
    const message = vaildateFormInputs(cellBarcode, productBarcode, amount);
    if (message) {
      alert(message);
      return;
    } else {
      addNewProductAction(cellBarcode, productBarcode, -amount);
      resetField();
      resetAmountField();
    }
  };


  return (
    <Form>
      <CellBarcodeInput />
      <ProductBarcodeInput />
      <AmountInput />

      <ButtonWrapper>
        <button
          className="btn btn-outline-secondary"
          onClick={addProduct}>
          Add Product
        </button>
        <button
          className="btn btn-outline-secondary"
          onClick={takeProduct}>
          Take Product
        </button>
      </ButtonWrapper>
    </Form>
  )
};

const Label = styled.label`
  > span {
    margin-left: 10px;
    font-size: 20px;
    font-weight: 600;
    color: #E4F1FE;
    vertical-align: bottom;
    text-transform: uppercase;
  }

  > input {
    display: inline-block;
    width: 200px;
    height: 40px;

    border: none;
    outline: none;
    border-bottom: 5px solid #BDC3C7;
    background-color: #34495E;

    font-size: 20px;
    font-weight: 600;
    color: #E4F1FE;
  }

  margin-bottom: 10px;
`;

const ButtonWrapper = styled.div`
  display: flex;
  margin-top: 30px;

  > button {
    display: inline-block;

    width: 200px;
    padding: 10px;
    margin-right: 10px;

    font-size: 20px;
    font-weight: 600;
    color: #34495E;

    &:hover {
      cursor: pointer;
    }
  }
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  width: 450px;
  padding: 5px;
  padding-bottom: 0;
  margin-right: 20px;
  margin-bottom: 40px;
`;

export default AddProductForm;
