import React from 'react';
import { createStore, createEvent, createStoreObject } from 'effector';
import { createComponent } from 'effector-react';
import './add-product-form.css';

const cellBarcodeInput = createStore('');
const productBarcodeInput = createStore('');
const amountInput = createStore('');

const changeCellBarcodeInput = createEvent('cell barcode product form');
const changeProductBarcodeInput = createEvent('product barcode product form');
const changeAmountInput = createEvent('amount input product form');
const resetField = createEvent('reset field product form');

cellBarcodeInput
  .on(changeCellBarcodeInput, (state, payload) => payload)
  .reset(resetField);

productBarcodeInput
  .on(changeProductBarcodeInput, (state, payload) => payload)
  .reset(resetField);

amountInput
  .on(changeAmountInput, (state, payload) => payload)
  .reset(resetField);


const CellBarcodeInput = createComponent(
  productBarcodeInput, (props, text) => {
    return (
      <label>
        Cell Barcode
        <input 
          type="text"
          value={productBarcodeInput}
          onChange={event => changeProductBarcodeInput(event.target.value)} 
        />
      </label>
    )
  }
)


const AddProductFormTest = createStoreConsumer = ({
  productBarcodeInput,
  cellBarcodeInput,
  amountInput
}) => {
  return (
    <form className="add-product-form">
      <label>
        Cell Barcode
        <input 
          type="text"
          value={productBarcodeInput}
          onChange={event => changeProductBarcodeInput(event.target.value)} />
      </label>
      <label>
        Product Barcode
        <input type="text"/>
      </label>
      <label>
        Amount
        <input type="text"/>
      </label>

      <button className="btn btn-outline-secondary">
        Add Product
      </button>
      <button className="btn btn-outline-secondary">
        Take Product
      </button>
    </form>
  )
}

const AddProductForm = () => {
  return (
    <form className="add-product-form">
      <label>
        Cell Barcode
        <input type="text"/>
      </label>
      <label>
        Product Barcode
        <input type="text"/>
      </label>
      <label>
        Amount
        <input type="text"/>
      </label>

      <button className="btn btn-outline-secondary">
        Add Product
      </button>
      <button className="btn btn-outline-secondary">
        Take Product
      </button>
    </form>
  )
};

export default AddProductFormTest;
